(ns day2
  (:require [clojure.string :as str]))

; Day 2 / #1 =====================================================================================================================

;; converts the string input to list of arrays with round data, aka ([A X] [B Y] ...)
(defn rounds [input]
  (for [round (str/split-lines input)]
    (str/split round #" ")
    )
  )

;; returns the score per action
(defn score [action]
  (case action
    "A" 1
    "B" 2
    "C" 3
    "X" 1
    "Y" 2
    "Z" 3
    )
  )

; 1 defeats 3
; 3 defeats 2
; 2 defeats 1
;; returns whether your pick is defeated by the opponent's pick (boolean)
(defn defeated? [opp you]
  (or
    (and (= opp 1) (= you 3))
    (and (= opp 3) (= you 2))
    (and (= opp 2) (= you 1)))
  )

;; returns the score for this round, based on the picks (using #score) of you and your opponent
(defn round-score [opp you]
  (cond
    (= opp you) (+ 3 you)                                 ; draw
    (defeated? opp you) you                               ; loss
    :else (+ 6 you)                                       ; win
    )
  )

;; calculate the total score
(defn total-score [round-map]
  (apply +
     (for [round (rounds round-map)]
       (round-score (score (get round 0)) (score (get round 1)))
       )
    )
  )

