;; returns the preferred pick for the preferred result (loss, win, draw)
(defn pick [opp res]
  (case res
    1 (case opp                                                      ; wanted result is a loss
      1 3
      3 2
      2 1)

    2 opp                                                            ; wanted result is a draw

    3 (case opp                                                      ; wanted result is a win
      3 1
      2 3
      1 2)
    )
  )

;; calculates the total score of the game using the new XYZ defs
(defn total-score-2 [round-map]
  (apply +
     (for [round (rounds round-map)
           :let [opp (score (get round 0))
                 you (pick opp (score (get round 1)))]]
       (round-score opp you)
       )
