(ns day9
  (:require [clojure.string :as str]))

; Day 9 / #1 =====================================================================================================================

(defn parse-directions [string]
  (flatten (for [line (str/split-lines string)
        :let [parts (str/split line #" ")
              direction (get parts 0)
              amount (Integer/parseInt (get parts 1))]]
    (repeat amount direction))))

(defn translate-direction [dir]
  (case dir
    "U" [0 1]
    "D" [0 -1]
    "R" [1 0]
    "L" [-1 0])
  )

(defn visit [dir h t]
  (let [translation (translate-direction dir)
        next-h (mapv + h translation)
        dx (Math/abs (int (- (get next-h 0) (get t 0))))
        dy (Math/abs (int (- (get next-h 1) (get t 1))))
        move? (or (> dx 1) (> dy 1))
        next-t (if move? h t)]
    [next-h next-t]))

(defn visited-count [directions]
  (loop [directions directions h [0 0] t [0 0] log []]
    (if (zero? (count directions))
      (count (distinct log))
      (let [visit (visit (first directions) h t)
            next-h (get visit 0)
            next-t (get visit 1)]
        (recur
          (drop 1 directions)
          next-h
          next-t
          (conj log next-t))))))

