; Day 9 / #2 =====================================================================================================================

(defn visited-count [directions]
  (loop [directions directions h [0 0] t [0 0] log []]
    (if (zero? (count directions))
      (count (distinct log))
      (let [visit (visit (first directions) h t)
            next-h (get visit 0)
            next-t (get visit 1)]
        (recur
          (drop 1 directions)
          next-h
          next-t
          (conj log next-t))))))